  const fetchItem = async () => {
    const query = new Moralis.Query("Announcement");
    query.descending("createdAt");
    query.skip(pageSize);
    query.limit(1);
    const result = await query.find();
    const res = JSON.parse(JSON.stringify(result));
    return res;
  };

